    if (args.depth > 10) {
      return callback({ url: args.url, err: new Error("Maximum recursion depth exceeded"), depth: args.depth, seen: args.seen });
    }
    var service = null;
    for (var key in self.map) {
      if (args.url.indexOf(key) !== -1) {
        service = key;
        break;
      }
    }
    if (!service) {
      return self.APIs.generic(args.url, function(err, expandedUrl) {
        if (err) {
          return callback({ url: args.url, err: err, depth: args.depth, seen: args.seen });
        }
        self.expand({ url: expandedUrl, depth: args.depth + 1, seen: args.seen }, callback);
      });
    }
    if (self.APIs[service]) {
      self.APIs[service](args.url, function(err, expandedUrl) {
        if (err) {
          return callback({ url: args.url, err: err, depth: args.depth, seen: args.seen });
        }
        self.expand({ url: expandedUrl, depth: args.depth + 1, seen: args.seen }, callback);
      });
    } else {
      return callback({ url: args.url, err: new Error("Unsupported URL shortening service"), depth: args.depth, seen: args.seen });
    }
  }
}

