function getServer(P) {
  const express = require('express');
  const path = require('path');
  const handlebars = require('handlebars');
  const app = express();
  const port = P || process.env.PORT || 8000;
  const isReferenceMode = port === 8000;
  const templateDir = isReferenceMode ? path.join(__dirname, 'templates/reference') : path.join(__dirname, 'templates/dev');
  const staticDir = isReferenceMode ? path.join(__dirname, 'static/reference') : path.join(__dirname, 'static/dev');

  app.set('view engine', 'hbs');
  app.engine('hbs', handlebars.engine());
  app.set('views', templateDir);
  app.use(express.static(staticDir));

  app.get('/:component', (req, res) => {
    const component = req.params.component;
    const config = {
      component,
      referenceMode: isReferenceMode,
      port
    };
    res.render('test', { config });
  });

  app.listen(port, () => {
    process.title = isReferenceMode ? 'regression-test-server-reference' : `regression-test-server-dev-${port}`;
  });

  return app;
}
