function setupExpressServer() {
  const express = require('express');
  const fs = require('fs');
  const path = require('path');
  const responseTime = require('response-time');

  const app = express();
  app.use(responseTime());

  const filePath = path.join(__dirname, 'CapitalOne_Digital_Skills_Infographic_BW.jpg');

  app.get('/stream', (req, res) => {
    const stream = fs.createReadStream(filePath);
    stream.pipe(res);
    stream.on('error', (err) => {
      res.status(500).send('Streaming error');
    });
  });

  app.get('/stream-manual', (req, res) => {
    const stream = fs.createReadStream(filePath);
    stream.on('data', (chunk) => res.write(chunk));
    stream.on('end', () => res.end());
    stream.on('error', (err) => res.status(500).send('Manual streaming error'));
  });

  app.get('/non-stream-async', (req, res) => {
    fs.readFile(filePath, (err, data) => {
      if (err) return res.status(500).send('Async read error');
      res.send(data);
    });
  });

  app.get('/non-stream-sync', (req, res) => {
    try {
      const data = fs.readFileSync(filePath);
      res.send(data);
    } catch (err) {
      res.status(500).send('Sync read error');
    }
  });

  app.listen(3000, () => console.log('Server on port 3000'));
}

