const express = require('express');
const rateLimit = require('express-rate-limit');
const cors = require('cors');
const puppeteer = require('puppeteer');
const path = require('path');

async function setupExpressServer() {
  const app = express();

  const limiter = rateLimit({
    windowMs: 15 * 60 * 1000,
    max: 100
  });
  app.use(limiter);
  app.use(cors());

  const browser = await puppeteer.launch();

  app.get('/query', async (req, res) => {
    try {
      const { url, selector } = req.query;
      if (!url || !selector) {
        return res.status(400).send('Missing URL or selector');
      }
      const page = await browser.newPage();
      await page.goto(url, { waitUntil: 'networkidle2', timeout: 60000 });
      await page.waitForSelector(selector, { timeout: 10000 });
      const elements = await page.$$(selector);
      const results = await Promise.all(elements.map(async (el) => {
        const text = await page.evaluate(e => e.innerText, el);
        const href = await page.evaluate(e => e.href, el);
        return { text, href };
      }));
      await page.close();
      res.json(results);
    } catch (error) {
      console.error(error);
      res.status(500).send('Error: ' + error.message);
    }
  });

  app.get('/fullHtml', async (req, res) => {
    try {
      const { url } = req.query;
      if (!url) {
        return res.status(400).send('Missing URL');
      }
      const page = await browser.newPage();
      await page.goto(url, { waitUntil: 'networkidle2', timeout: 60000 });
      const content = await page.content();
      await page.close();
      res.send(content);
    } catch (error) {
      console.error(error);
      res.status(500).send('Error: ' + error.message);
    }
  });

  app.get('/', (req, res) => {
    res.sendFile(path.join(__dirname, 'index.html'));
  });

  const PORT = process.env.PORT || 3000;
  app.listen(PORT, () => {
    console.log(`Server running on port ${PORT}`);
  });
}

setupExpressServer();

