  const axios = require('axios');
  const app = express();
  const port = 3000;

  app.use(cors());

  app.get('/wikipedia_summaries', async (req, res) => {
    const keyword = req.query.keyword;
    if (!keyword) return res.status(400).json({ error: 'Keyword is required' });

    try {
      const searchRes = await axios.get('https://en.wikipedia.org/w/api.php', {
        params: { action: 'opensearch', search: keyword, format: 'json' }
      });
      const [_, titles, __, urls] = searchRes.data;
      const results = titles.map((title, i) => ({ title, url: urls[i] }));

      const detailedResults = await Promise.all(results.map(async (page) => {
        const extractRes = await axios.get('https://en.wikipedia.org/w/api.php', {
          params: {
            action: 'query', prop: 'extracts', titles: page.title, format: 'json',
            exintro: true, exlimit: 1
          }
        });
        const pageData = extractRes.data.query.pages;
        const pageId = Object.keys(pageData)[0];
        const summary = pageData[pageId].extract || 'No summary available';

        const catsRes = await axios.get('https://en.wikipedia.org/w/api.php', {
          params: { action: 'query', prop: 'categories', titles: page.title, format: 'json' }
        });
        const categories = catsRes.data.query.pages[pageId]?.categories?.map(c => c.title) || [];

        return { ...page, summary, categories };
      }));

      res.json(detailedResults);
