    fileStream.write(`Connection ID: ${connId}\nTime: ${new Date().toISOString()}\n\n`);
    socket.pipe(fileStream);

    fileStreams.set(connId, fileStream);

    socket.on('close', () => {
      fileStream.end();
      console.log(`Connection ${connId} closed`);
      activeConnections.delete(connId);
      fileStreams.delete(connId);
