  const allChannels = [];

  for (const provider of providers) {
    try {
      const response = await axios.post(provider.url, { date });
      const $ = cheerio.load(response.data);

      $(provider.channelSelector).each((_, element) => {
        const number = $(element).attr(provider.numberAttr) || 'unknown';
        const name = $(element).find(`.${provider.nameClass}`).text().trim();
        const slug = name.toLowerCase()
