          .replace(/[^a-z0-9-]/g, '');
        const siteId = `${number}-${slug}`;

        allChannels.push({
          lang: provider.lang,
          name,
          site_id: siteId
        });
      });
    } catch (error) {
      console.error(`Error with ${provider.name}:`, error.message);
    }
  }

  return _.uniqBy(allChannels, 'site_id');
}

