const http = require('http');
const fs = require('fs');
const path = require('path');
const send = require('send');
const marked = require('marked');
const URL = require('url').URL;

class Server {
  constructor(config) {
    this.config = config;
    this.path = path.resolve(config.path);
    this._http = http.createServer(this.handleRequest.bind(this));
    this.sockets = [];
    this.updateServer();
  }

  updateServer() {
    this._http.on('connection', (socket) => {
      this.sockets.push(socket);
      socket.once('close', () => this.removeSocket(socket));
      socket.on('error', () => this.removeSocket(socket));
    });
  }

  removeSocket(socket) {
    const index = this.sockets.indexOf(socket);
    if (index !== -1) {
      this.sockets.splice(index, 1);
    }
  }

  handleRequest(req, res) {
    const { pathname } = new URL(req.url, `http://${req.headers.host || 'localhost'}`);
    const base = path.resolve(this.path);
    const absolutePath = path.resolve(base, pathname);
    if (!absolutePath.startsWith(base)) {
      res.writeHead(403, { 'Content-Type': 'text/plain' });
      return res.end('403 Forbidden');
    }

    if (pathname.endsWith('.md')) {
      fs.stat(absolutePath, (err, stats) => {
        if (err) {
          res.writeHead(err.code === 'ENOENT' ? 404 : 500, { 'Content-Type': 'text/plain' });
          return res.end(err.code === 'ENOENT' ? '404 Not Found' : '500 Internal Server Error');
        }
        if (stats.isFile()) {
          fs.readFile(absolutePath, 'utf8', (err, data) => {
            if (err) {
              res.writeHead(500, { 'Content-Type': 'text/plain' });
              return res.end('500 Internal Server Error');
            }
            res.writeHead(200, { 'Content-Type': 'text/html' });
            return res.end(marked(data));
          });
        } else {
          res.writeHead(404, { 'Content-Type': 'text/plain' });
          res.end('404 Not Found');
        }
      });
    } else {
      send(req, pathname, { root: base })
        .on('error', (err) => {
          res.writeHead(err.status || 500, { 'Content-Type': 'text/plain' });
          res.end(err.message || '500 Internal Server Error');
        })
        .pipe(res);
    }
  }

  start(callback) {
    this._http.listen(this.config.port, callback);
  }

  stop(callback) {
    this._http.close(callback);
    this.sockets.forEach(socket => socket.destroy());
    this.sockets = [];
  }
}

module.exports = Server;
