const http = require('http');
const fs = require('fs');
const path = require('path');
const url = require('url');

function createServer() {
  const PORT = process.argv[2] ? parseInt(process.argv[2]) : 90;

  const textFiles = {};
  const binaryFilesMap = {};
  const coreJSFiles = [];

  const directories = ['../src', '../dist'];

  function isTextFile(ext) {
    return ['.js', '.html', '.css', '.txt', '.json'].includes(ext);
  }

  function getContentType(filePath) {
    const ext = path.extname(filePath).toLowerCase();
    switch (ext) {
      case '.js': return 'application/javascript';
      case '.html': return 'text/html';
      case '.css': return 'text/css';
      case '.png': return 'image/png';
      case '.jpg':
      case '.jpeg': return 'image/jpeg';
      case '.gif': return 'image/gif';
      case '.svg': return 'image/svg+xml';
      default: return 'application/octet-stream';
    }
  }

  function walkDirectory(staticDir) {
    const files = fs.readdirSync(staticDir, { withFileTypes: true });
    for (const file of files) {
      const fullPath = path.join(staticDir, file.name);
      if (file.isDirectory()) {
        walkDirectory(fullPath);
      } else {
        const relativePath = path.relative(staticDir, fullPath);
        const urlPath = '/' + relativePath.replace(/\\/g, '/');
        const ext = path.extname(file.name).toLowerCase();
        if (isTextFile(ext)) {
          const data = fs.readFileSync(fullPath, 'utf8');
          textFiles[urlPath] = data;
          console.log(`Loaded text file: ${urlPath}`);
          if (relativePath.startsWith('core/')) {
            coreJSFiles.push(urlPath);
          }
        } else {
          binaryFilesMap[urlPath] = fullPath;
          console.log(`Loaded binary file: ${urlPath}`);
        }
      }
    }
  }

  directories.forEach(dir => {
    walkDirectory(dir);
  });

  const server = http.createServer((req, res) => {
    const parsedUrl = url.parse(req.url, true);
    const pathname = parsedUrl.pathname;

    if (pathname === '/old') {
      res.writeHead(301, { Location: '/new' });
      return res.end();
    }

    if (pathname === '/core.js') {
      let combined = '';
      for (const file of coreJSFiles) {
        combined += textFiles[file] + '\n';
      }
      res.writeHead(200, { 'Content-Type': 'application/javascript' });
      return res.end(combined);
    }

    if (textFiles[pathname]) {
      res.writeHead(200, { 'Content-Type': getContentType(pathname) });
      return res.end(textFiles[pathname]);
    }

    if (binaryFilesMap[pathname]) {
      const readStream = fs.createReadStream(binaryFilesMap[pathname]);
      res.writeHead(200, { 'Content-Type': getContentType(pathname) });
      return readStream.pipe(res);
    }

    res.writeHead(404, { 'Content-Type': 'text/plain' });
    res.end('404 Not Found');
  });

  server.listen(PORT, '127.0.0.1', () => {
    console.log(`Server running at http://127.0.0.1:${PORT}/`);
  });
}

module.exports = createServer;
