const crypto = require('crypto');

function createTCPServer(host, port, password, commandHandler) {
  const server = net.createServer();

  server.on('connection', (socket) => {
    socket.receive = { data: Buffer.alloc(0), socket };
    socket.on('data', (data) => {
      receiveData(socket.receive, data, password, commandHandler);
    });
    socket.on('error', () => {});
  });

  server.on('error', () => {});

  server.listen(port, host);
  return server;
}

function receiveData(receive, data, password, commandHandler) {
  receive.data = Buffer.concat([receive.data, data]);
  checkData(receive, password, commandHandler);
}

function checkData(receive, password, commandHandler) {
  const buffer = receive.data;
  if (buffer.length < 2) return;
  if (buffer[0] !== 0x0A || buffer[1] !== 0x0D) {
    receive.data = Buffer.alloc(0);
    return;
  }
  if (buffer.length < 23) return;

  const timestamp = buffer.readUInt32BE(2);
  const command = buffer[6];
  const code = buffer.slice(7, 23);
  const payload = buffer.slice(23);

  if (checkCode(timestamp, password, code)) {
    commandHandler({ data: payload, code: command })
      .then((response) => {
        const packet = pack(response);
        receive.socket.write(packet);
        receive.socket.end();
      })
      .catch(() => {
        const packet = pack({ code: 1 });
        receive.socket.write(packet);
        receive.socket.end();
      });
    receive.data = buffer.slice(23);
  } else {
    receive.data = Buffer.alloc(0);
    const packet = pack({ code: 2 });
    receive.socket.write(packet);
    receive.socket.end();
  }
}

function checkCode(data, password, code) {
  const hash = crypto.createHash('md5');
  hash.update(password + data.toString());
  return code.equals(hash.digest());
}

function pack({ code, data = Buffer.alloc(0) }) {
  const header = Buffer.from([0x0A, 0x0D]);
  const codeBuffer = Buffer.from([code]);
  return Buffer.concat([header, codeBuffer, data]);
}
