const net = require('net');
const { EventEmitter } = require('events');

class TcpReader extends EventEmitter {
  constructor(opts, transporter, PromiseCtor, logger) {
    super();
    this.opts = opts || {};
    this.transporter = transporter;
    this.Promise = PromiseCtor;
    this.logger = logger;
    this.sockets = [];
    this.server = net.createServer();
    this.server.on('connection', (socket) => this.onTcpClientConnected(socket));
    this._setupServerEvents();
  }

  _setupServerEvents() {
    this.server.on('error', (err) => {
      this.logger.error('Server error:', err);
      this.emit('error', err);
    });

    this.server.on('listening', () => {
      this.emit('listening');
    });
  }

  listen() {
    return new this.Promise((resolve, reject) => {
      this.server.listen(this.opts.port, () => {
        const address = this.server.address();
        this.logger.info(`Server listening on port ${address.port}`);
        resolve(address.port);
      });
    });
  }

  onTcpClientConnected(socket) {
    this.sockets.push(socket);
    socket._buffer = Buffer.alloc(0);

    socket.on('data', (data) => {
      socket._buffer = Buffer.concat([socket._buffer, data]);

      let bufferString = socket._buffer.toString();
      const lines = bufferString.split('\n');
      socket._buffer = Buffer.from(lines.pop());

      for (const line of lines) {
        if (line.length > this.opts.maxPacketSize) {
          this.logger.error('Received message exceeds maxPacketSize');
          this.closeSocket(socket);
          return;
        }

        try {
          const message = JSON.parse(line);
          this.emit('message', message);
        } catch (e) {
          this.logger.error('Failed to parse message', e);
          this.closeSocket(socket);
          return;
        }
      }
    });

    socket.on('error', (err) => {
      this.logger.error('Socket error:', err);
      this.closeSocket(socket);
    });

    socket.on('close', () => {
      this.logger.info('Client disconnected');
      this.closeSocket(socket);
    });
  }

  closeSocket(socket) {
    const index = this.sockets.indexOf(socket);
    if (index !== -1) {
      this.sockets.splice(index, 1);
    }
    if (!socket.destroyed) {
      socket.destroy();
    }
  }

  close() {
    this.server.close(() => {
      this.sockets.forEach(socket => this.closeSocket(socket));
      this.sockets.length = 0;
      this.emit('close');
    });
  }
}

module.exports = TcpReader;

