      this.emit('error', err);
    });

    this.tcpServer.listen(this.port, this.host, () => {
      this.emit('listening');
      if (cb) cb();
    });
  }

  stop(force, cb) {
    if (typeof force !== 'boolean') {
      cb = force;
      force = false;
    }

    if (!this.tcpServer) return cb();

    if (force) {
      for (const socket of this.sockets) {
        socket.destroy();
      }
    }

    this.tcpServer.close((err) => {
      this.emit('close', err);
      this.tcpServer = null;
      cb(err);
    });
  }
