            this.emit('log', 'Client connected');
            socket.dataBuffer = '';

            socket.on('data', (data) => {
                socket.dataBuffer += data.toString();
                let index;
                while ((index = socket.dataBuffer.indexOf('\n')) !== -1) {
                    const line = socket.dataBuffer.slice(0, index).trim();
                    socket.dataBuffer = socket.dataBuffer.slice(index + 1);
                    if (line) {
                        this.processCommand(line, socket);
                    }
                }
            });

            socket.on('error', (err) => {
                this.emit('log', `Socket error: ${err.message}`);
            });

            socket.on('close', () => {
                this.emit('log', 'Client disconnected');
            });
        });
    }

    processCommand(line, socket) {
        const parts = line.split(' ');
        const command = parts[0];
        const params = parts.slice(1);
        const callback = (response) => {
            socket.write(response);
        };
        this.emit('command', command, params, {}, callback);
    }
