var net = require('net');
var weak;

class DNodeServer {
    constructor(cons, opts) {
        if (!opts) opts = {};

        if (opts.weak !== false && !opts.proto) {
            if (!weak) {
                weak = require("weak");
            }

            opts.proto = {};
            opts.proto.wrap = function (cb, id) {
                var proto = this;
                return weak(cb, function () {
                    proto.cull(id);
                });
            };
            opts.proto.unwrap = function (ref, id) {
                var cb = weak.get(ref);
                return cb || function () {};
            };
        }

        this.cons = cons;
        this.opts = opts;
        this.server = net.createServer(this._handleConnection.bind(this));
        this.server.sessions = {};
    }

    _handleConnection(stream) {
        var d = new dnode(this.cons, this.opts);
        do { d.id = this._randomId(); }
        while (this.server.sessions[d.id]);

        this.server.sessions[d.id] = d;
        d.on('end', () => {
            delete this.server.sessions[d.id];
        });

        d.on('local', (ref) => {
            this.server.emit('local', ref, d);
        });

        d.on('remote', (remote) => {
            this.server.emit('remote', remote, d);
        });

        stream.on('error', (err) => {
            if (err && err.code === 'EPIPE') return;
            d.emit('error', err);
        });

        d.stream = stream;
        stream.pipe(d);
        d.pipe(stream);
    }

    listen(port, host, callback) {
        if (typeof port === 'number' && port >= 0) {
            this.server.listen(port, host);
        } else if (typeof port === 'string') {
            this.server.listen(port);
        } else {
            throw new Error('no port or path provided');
        }

        if (callback) {
            this.server.on('listening', callback);
        }

        return this.server;
    }

    _randomId() {
        var s = '';
        for (var i = 0; i < 4; i++) {
            s += Math.random().toString(16).slice(2);
        }
        return s;
    }
}

