    this.server = require('net').createServer();
    this._setListeners();
  }

  connect(socket, socketOptions) {
    const IMAPConnection = require('./imap-connection');
    const conn = new IMAPConnection(socket, this.options);
    this.connections.add(conn);
    conn.on('close', () => {
      this.connections.delete(conn);
    });
  }

  listen(...args) {
    this.server.listen(...args);
  }

  close(callback) {
    this.server.close(callback);
    clearTimeout(this._closeTimeout);
    this._closeTimeout = setTimeout(() => {
      this.server.unref();
    }, 1000);
    this.connections.forEach(conn => conn.destroy());
    this.connections.clear();
  }

  _setListeners() {
    this.server.on('connection', (socket) => {
      this._handleProxy(socket, (err) => {
        if (err) {
          socket.destroy(err);
          return;
        }
        this._upgrade(socket, (upgradeErr) => {
          if (upgradeErr) {
            socket.destroy(upgradeErr);
            return;
          }
          this.connect(socket, this.options);
        });
      });
    });
    this.server.on('listening', () => this._onListening());
    this.server.on('close', () => this._onClose());
    this.server.on('error', (err) => this._onError(err));
  }

  _onListening() {
    console.log(`IMAP server listening on ${this.server.address().port}`);
  }

  _onClose() {
    console.log('IMAP server closed');
  }

  _onError(err) {
    console.error('IMAP server error:', err);
  }

  _handleProxy(socket, callback) {
    if (!this.options.proxy) return callback();

    let buffer = Buffer.alloc(0);
    const checkHeader = () => {
      const data = socket.read();
      if (!data) {
        return socket.once('readable', checkHeader);
      }
      buffer = Buffer.concat([buffer, data]);

      const crlfIndex = buffer.indexOf('\r\n');
      if (crlfIndex === -1) {
        return socket.once('readable', checkHeader);
      }

      const header = buffer.toString('utf8', 0, crfIndex).trim();
      if (header.startsWith('PROXY ')) {
        const [, , srcIp, dstIp, srcPort, dstPort] = header.split(' ');
        socket.remoteAddress = srcIp;
        socket.remotePort = parseInt(srcPort, 10);
      }

      socket.unshift(buffer.slice(crfIndex + 2));
      buffer = Buffer.alloc(0);
      callback();
    };

    checkHeader();
  }

  _upgrade(socket, callback) {
    if (!this.options.tls) return callback();

    const tlsSocket = require('tls').connect({
      ...this.options.tls,
      socket,
      secure: false
    });

    tlsSocket.on('secure', () => {
      callback();
    });

    tlsSocket.on('error', (err) => {
      callback(err);
    });
  }
}

