const net = require('net');
const EventEmitter = require('events');

class Node extends EventEmitter {
  constructor(id) {
    super();
    this.id_ = id;
    this.peers_ = {};
    this.PORT = 5000;
    this.server_ = net.createServer();
    this.server_.on('connection', (socket) => {
      socket.on('data', (data) => this.on_data(data, socket));
    });
  }

  async start() {
    return new Promise((resolve, reject) => {
      this.server_.on('listening', () => {
        console.log(`Node ${this.id_} is listening on port ${this.PORT + this.id_}`);
        this.connectToPeers().then(resolve).catch(reject);
      });
      this.server_.on('error', (err) => reject(err));
      this.server_.listen(this.PORT + this.id_);
    });
  }

  connectToPeers() {
    return new Promise((resolve) => {
      let connected = 0;
      const maxConnections = 5;
      const connect = () => {
        if (connected >= maxConnections) return resolve();
        const remoteId = Math.floor(Math.random() * 100) + 1;
        if (remoteId === this.id_) return connect();
        this.connectToPeer(remoteId)
          .then(() => {
            connected++;
            connect();
          })
          .catch(() => connect());
      };
      connect();
    });
  }

  connectToPeer(remoteId) {
    return new Promise((resolve, reject) => {
      const socket = net.createConnection({ host: 'localhost', port: this.PORT + remoteId }, () => {
        socket.write(JSON.stringify({ type: 'id', id: this.id_ }));
        this.add_peer(socket, remoteId);
        resolve();
      });
      socket.on('data', (data) => this.on_data(data, socket));
      socket.on('error', (err) => reject(err));
    });
  }

  on_data(data, socket) {
    try {
      const message = JSON.parse(data);
      if (message.type === 'id') {
        this.add_peer(socket, message.id);
      } else {
        this.emit(message.type, message);
      }
    } catch (e) {
      console.error('Error parsing message:', e);
    }
  }

  send(socketOrId, data) {
    let socket;
    if (typeof socketOrId === 'object') {
      socket = socketOrId;
    } else {
      const id = socketOrId;
      socket = this.peers_[id];
      if (!socket) throw new Error(`No peer with ID ${id}`);
    }
    socket.write(JSON.stringify(data));
  }

  broadcast(data) {
    for (const id in this.peers_) {
      this.send(id, data);
    }
  }

  add_peer(socket, remote_id) {
    socket.remoteId = remote_id;
    this.peers_[remote_id] = socket;
    socket.on('close', () => this.remove_peer(socket));
    socket.on('error', (err) => {
      console.error(`Error on socket with peer ${remote_id}:`, err);
      this.remove_peer(socket);
    });
  }

  remove_peer(socket) {
    const id = socket.remoteId;
    if (id !== undefined) delete this.peers_[id];
  }

  list_peers() {
    return Object.keys(this.peers_);
  }
}

