    console.log(`Cached version data to ${versionsFilePath}`);

    const nonDeprecatedVersions = versionsData.filter(v => !v.isDeprecated);

    for (const v of nonDeprecatedVersions) {
      const version = v.version;
      const schemaUrl = SCHEMA_URL_TEMPLATE.replace('{version}', version);
      const schemaResponse = await axios.get(schemaUrl);
      const schemaData = schemaResponse.data;

      const schemaFilePath = path.join(CACHE_DIR, `schema-${version}.json`);
      await fs.writeFile(schemaFilePath, JSON.stringify(schemaData, null, 2));
      console.log(`Cached schema for version ${version} to ${schemaFilePath}`);
    }

    console.log('All data cached successfully');
  } catch (error) {
    console.error('Error during fetch/cache process:', error.message);
    process.exit(1);
  }
