      this.server = server;
      this.socket = server;
      this.socket.on('connection', (sock) => this.responder(sock));
      this.log('Attached to existing server');
      if (cb) cb();
    } else {
      this.port = port || this.port;
      this.socket = net.createServer((sock) => this.responder(sock));
      this.socket.listen(this.port, () => {
        this.log(`Policy server listening on port ${this.port}`);
        if (cb) cb();
      });
    }
    return this;
  }

  responder(socket) {
    socket.on('data', (data) => {
      if (data.length >= 15) {
        const firstFour = data.slice(0, 4);
        if (
          firstFour[0] === 0 &&
          firstFour[1] === 0 &&
          firstFour[2] === 0 &&
          firstFour[3] === 6
        ) {
