class ServerDiscovery {
  constructor() {
    this.nearbyServers = {};
    this.lastDiscoveryBroadcast = 0;
    this.broadcastIP = this.calcBroadcastIP();
    this.discoveryListener = null;
  }

  setupDiscovery(callback) {
    const dgram = require('dgram');
    this.discoveryListener = dgram.createSocket('udp4');
    this.discoveryListener.on('error', (err) => {
      console.error(`UDP discovery listener error: ${err.stack}`);
      this.discoveryListener.close();
    });
    this.discoveryListener.on('message', (msg, rinfo) => {
      this.discoveryReceive(msg, rinfo);
    });
    const PORT = 30001;
    this.discoveryListener.bind(PORT, () => {
      if (callback) callback();
    });
  }

  discoveryBroadcast(type, message, callback) {
    const dgram = require('dgram');
    const client = dgram.createSocket('udp4');
    client.setBroadcast(true);
    const data = JSON.stringify({ ...message, type });
    const buffer = Buffer.from(data);
    client.send(buffer, 0, buffer.length, 30001, this.broadcastIP, (err) => {
      if (err) console.error(`Broadcast error: ${err}`);
      if (callback) callback(err);
      client.close();
    });
  }

  discoveryReceive(msg, rinfo) {
    try {
      const data = JSON.parse(msg.toString());
      if (data.type === 'heartbeat' && data.hostname) {
        this.nearbyServers[data.hostname] = {
          ...data,
          lastSeen: Date.now()
        };
      }
    } catch (e) {
      console.error('Failed to parse discovery message:', e);
    }
  }

  shutdownDiscovery() {
    if (this.discoveryListener) {
      this.discoveryListener.close();
      this.discoveryListener = null;
    }
  }

  calcBroadcastIP() {
    const os = require('os');
    const interfaces = os.networkInterfaces();
    for (const dev in interfaces) {
      for (const details of interfaces[dev]) {
        if (!details.internal && details.family === 'IPv4') {
          const [ip] = details.address.split('.');
          return `${ip}.255`;
        }
      }
    }
    return '255.255.255.255';
  }
}

