class UdpPoller {
  constructor(target, timeout, callback) {
    this.target = target;
    this.timeout = timeout;
    this.callback = callback;
    this.udpServer = null;
    this.targetHost = '';
    this.targetPort = 0;
    this.timer = null;
    this.pending = false;
  }

  static get type() {
    return 'udp';
  }

  static validateTarget(target) {
    if (typeof target !== 'string') return false;
    try {
      const url = new URL(target);
      return url.protocol === 'udp:';
    } catch (e) {
      return false;
    }
  }

  initialize() {
    if (typeof this.target === 'string') {
      const url = new URL(this.target);
      this.targetHost = url.hostname;
      this.targetPort = parseInt(url.port, 10) || 0;
    } else if (this.target && typeof this.target === 'object') {
      this.targetHost = this.target.host || '';
      this.targetPort = this.target.port || 0;
    }
    this.udpServer = dgram.createSocket('udp4');
    this.udpServer.on('message', (msg, rinfo) => this.onResponseCallback(msg, rinfo));
    this.udpServer.on('error', (err) => {
      this.callback(err);
    });
    this.udpServer.bind(0);
  }

  poll() {
    if (this.pending) {
      return;
    }
    if (!this.udpServer) {
      this.initialize();
    }
    const checkAndSend = () => {
      try {
        const info = this.udpServer.address();
        this.doPoll();
      } catch (e) {
        this.udpServer.once('listening', () => {
          this.doPoll();
        });
      }
    };
    checkAndSend();
  }

  doPoll() {
    this.pending = true;
    const message = Buffer.from('ping');
    this.timer = setTimeout(() => this.timeoutReached(), this.timeout);
    this.udpServer.send(message, this.targetPort, this.targetHost, (err) => {
      if (err) {
        this.callback(err);
        this.pending = false;
      }
    });
  }

  onResponseCallback(message, sender) {
    if (!this.pending) return;
    if (message.toString() === 'pong') {
      clearTimeout(this.timer);
      this.pending = false;
      this.callback(null, { data: message, sender });
    }
  }

  timeoutReached() {
    if (!this.pending) return;
    this.pending = false;
    this.callback(new Error('Timeout waiting for pong response'));
  }
}

module.exports = UdpPoller;
