const dgram = require('dgram');
const EventEmitter = require('events');

class MasterIPC extends EventEmitter {
  constructor(options) {
    super();
    this.options = options || {};
    this.children = [];
    this._sock = null;
  }

  IPCSocket() {
    if (this._sock) return this._sock;
    this._sock = dgram.createSocket('udp4');
    this.sock = this._sock;
    this._sock.on('message', (msg, rinfo) => {
      try {
        const data = JSON.parse(msg);
        if (data.type === 'connect') {
          this.connect({ id: data.id, address: rinfo.address, port: rinfo.port });
        } else if (data.type === 'method_call') {
          this.invoke(data.method, data.args, data.workerId);
        } else if (data.type === 'master_killed') {
          this.masterKilled(data.payload);
        }
      } catch (e) {
        console.error('Error parsing message:', e);
      }
    });
    return this._sock;
  }

  setupIPC() {
    const sock = this.IPCSocket();
    sock.bind(this.options.port, this.options.address, () => {
      this.emit('start');
    });
  }

  call(id, method, ...args) {
    const worker = this.children.find(c => c.id === id);
    if (!worker) {
      console.warn(`Worker with id ${id} not found`);
      return;
    }
    const message = JSON.stringify({
      type: 'method_call',
      method,
      args,
      workerId: id
    });
    this.sock.send(message, 0, message.length, worker.port, worker.address, (err) => {
      if (err) console.error('Error sending message:', err);
    });
  }

  invoke(method, args, workerId) {
    if (this[method]) {
      this[method].apply(this, [args, workerId]);
    } else {
      console.warn(`Method ${method} not found on MasterIPC`);
    }
  }

  connect(worker) {
    this.children.push(worker);
    this.emit('worker connected', worker);
  }

  connectMaster(sig) {
    const sock = this.IPCSocket();
    const checkAndKill = () => {
      if (sock.address) {
        process.kill(process.ppid, sig);
      } else {
        const onStarted = () => {
          this.off('start', onStarted);
          process.kill(process.ppid, sig);
        };
        this.on('start', onStarted);
      }
    };
    checkAndKill();
  }

  masterKilled(data) {
    this.emit('restart', data);
  }
}

module.exports = MasterIPC;
