class WebSocketClient {
  constructor() {
    this.ws = null;
    this.executorMap = new Map();
    this.bufferMap = new Map();
    this.timer = null;
    this.requestIdCounter = 0;
  }

  async connect() {
    return new Promise((resolve, reject) => {
      this.ws = new WebSocket('wss://speech.platform.bing.com/synthesize', {
        headers: {
          'Authorization': 'Bearer YOUR_ACCESS_TOKEN',
          'Content-Type': 'application/ssml+xml'
        }
      });

      this.timer = setTimeout(() => {
        this.ws.terminate();
        reject(new Error('Connection timeout'));
      }, 10000);

      this.ws.on('open', () => {
        clearTimeout(this.timer);
        resolve(this.ws);
      });

      this.ws.on('error', (err) => {
        reject(err);
      });

      this.ws.on('close', () => {
        reject(new Error('Connection closed'));
      });

      this.ws.on('message', (data, isBinary) => {
        const requestId = this.getRequestIdFromMessage(data, isBinary);
        if (!requestId) return;

        if (isBinary) {
          const existing = this.bufferMap.get(requestId) || Buffer.alloc(0);
          this.bufferMap.set(requestId, Buffer.concat([existing, data]));
        } else {
          const text = data.toString();
          if (text.includes('Session closed')) {
            const buffer = this.bufferMap.get(requestId);
            const executor = this.executorMap.get(requestId);
            if (executor) {
              executor.resolve(buffer);
              this.executorMap.delete(requestId);
              this.bufferMap.delete(requestId);
            }
          } else if (text.includes('error')) {
            const executor = this.executorMap.get(requestId);
            if (executor) {
              executor.reject(new Error(text));
              this.executorMap.delete(requestId);
              this.bufferMap.delete(requestId);
            }
          }
        }
      });
    });
  }

  getRequestIdFromMessage(data, isBinary) {
    if (!isBinary) {
      const text = data.toString();
      const match = text.match(/<speak.*?requestId="([^"]+)"/);
      if (match) return match[1];
    } else {
      try {
        const str = data.toString('utf8', 0, 100);
        const match = str.match(/requestId=([^&]+)/);
        if (match) return match[1];
      } catch (e) {}
    }
    return null;
  }

  async sendRequest(ssml, format) {
    const requestId = this.requestIdCounter++;
    return new Promise((resolve, reject) => {
      this.executorMap.set(requestId, { resolve, reject });
      this.bufferMap.set(requestId, Buffer.alloc(0));

      const setupMessage = JSON.stringify({
        format,
        language: 'en-US',
        name: 'Microsoft Server Speech Text to Speech',
        request: {
          id: requestId
        }
      });
      this.ws.send(setupMessage);

      const ssmlWithId = `<speak requestId="${requestId}">${ssml}</speak>`;
      this.ws.send(ssmlWithId);
    });
  }
}

