const EventEmitter = require('events');
const WebSocket = require('ws');

class Chrome extends EventEmitter {
  constructor(options, notifier) {
    super();
    this.host = options?.host || 'localhost';
    this.port = options?.port || 9222;
    this.secure = options?.secure || false;
    this._notifier = notifier;
    this._callbacks = {};
    this._nextCommandId = 1;
    this.webSocketUrl = `${this.secure ? 'wss' : 'ws'}://${this.host}:${this.port}/devtools/hub`;
    this._ws = null;
  }

  async _connectToWebSocket() {
    this._ws = new WebSocket(this.webSocketUrl);

    this._ws.on('open', () => {
      this.emit('ready');
    });

    this._ws.on('message', (data) => {
      this._handleMessage(JSON.parse(data));
    });

    this._ws.on('error', (err) => {
      this.emit('error', err);
    });

    this._ws.on('close', (code, reason) => {
      this.emit('close', code, reason);
    });
  }

  _handleMessage(message) {
    if (message.id !== undefined) {
      const callback = this._callbacks[message.id];
      if (callback) {
        if (message.error) {
          callback(new Error(message.error.message));
        } else {
          callback(null, message.result);
        }
        delete this._callbacks[message.id];
      }
    } else if (message.method) {
      this.emit(message.method, message.params);
      if (this._notifier && this._notifier[message.method]) {
        this._notifier[message.method](message.params);
      }
    }
  }

  _enqueueCommand(method, params, sessionId, callback) {
    const commandId = this._nextCommandId++;
    const command = {
      id: commandId,
      method,
      params,
    };
    if (sessionId) {
      command.sessionId = sessionId;
    }
    this._ws.send(JSON.stringify(command));
    if (callback) {
      this._callbacks[commandId] = callback;
    }
  }

  send(method, ...args) {
    let sessionId, params, userCallback;

    if (args.length > 0 && typeof args[args.length - 1] === 'function') {
      userCallback = args.pop();
    }

    if (args.length >= 1) {
      if (typeof args[0] === 'string') {
        sessionId = args[0];
        if (args.length >= 2) {
          params = args[1];
        }
      } else {
        params = args[0];
      }
    }

    if (userCallback) {
      this._enqueueCommand(method, params, sessionId, userCallback);
      return;
    } else {
      return new Promise((resolve, reject) => {
        this._enqueueCommand(method, params, sessionId, (err, result) => {
          if (err) {
            reject(err);
          } else {
            resolve(result);
          }
        });
      });
    }
  }
}

module.exports = Chrome;

