function runParallelTests() {
  const socket = new WebSocket('ws://testserver.com/socket');
  const maxParallelRuns = 5;
  const subscriptionKey = Date.now();
  let queue = [];
  let numRunning = 0;

  const testContainer = document.getElementById('test-results');
  testContainer.innerHTML = '';

  document.querySelectorAll('.test-case').forEach((el, index) => {
    const testId = `test_${index}`;
    queue.push({
      url: el.dataset.url,
      id: testId
    });

    const testDiv = document.createElement('div');
    testDiv.className = 'test-entry';
    testDiv.innerHTML = `
      <div class="status" id="status_${testId}">Pending</div>
      <div class="result" id="result_${testId}"></div>
    `;
    testContainer.appendChild(testDiv);
  });

  function processQueue() {
    while (numRunning < maxParallelRuns && queue.length > 0) {
      const test = queue.shift();
      numRunning++;
      openTest(test);
    }
  }

  function openTest(test) {
    const testUrl = `${test.url}?subscriptionKey=${subscriptionKey}&testId=${test.id}`;
    window.open(testUrl, '_blank');
  }

  socket.onopen = function() {
    socket.send(JSON.stringify({ type: 'subscribe', key: subscriptionKey }));
    processQueue();
  };

  socket.onmessage = function(event) {
    const message = JSON.parse(event.data);
    if (message.type === 'test_status') {
      updateTestStatus(message.testId, message.status);
    } else if (message.type === 'test_result') {
      updateTestResult(message.testId, message.result);
    } else if (message.type === 'test_complete') {
      numRunning--;
      processQueue();
    }
  };

  function updateTestStatus(testId, status) {
    const el = document.getElementById(`status_${testId}`);
    if (el) {
      el.textContent = status;
      el.className = `status ${status.toLowerCase()}`;
    }
  }

  function updateTestResult(testId, result) {
    const el = document.getElementById(`result_${testId}`);
    if (el) {
      el.textContent = result;
    }
  }
}

