    const batch = requests.map(req => {
      const fullMethod = this._namespace + req.method;
      return this._formatData(fullMethod, req.params, req.id);
    });
    this._send(batch);
    requests.forEach(req => {
      this._handles[req.id] = { successCallback, errorCallback };
    });
  }

