function connectServer(port) {
    const ws = new WebSocket(`ws://localhost:${port}`);
    window.wsSocket = ws;

    ws.onopen = function () {
        console.log("WebSocket connected");
        GlobalEvents.emit("connection-established");
    };

    ws.onmessage = function (event) {
        const message = JSON.parse(event.data);
        switch (message.type) {
            case "config":
                GlobalEvents.emit("config-update", message.payload);
                break;
            case "checkResult":
                GlobalEvents.emit("check-result", message.payload);
                break;
            case "moduleStart":
                GlobalEvents.emit("module-start", message.moduleName);
                break;
            case "moduleEnd":
                GlobalEvents.emit("module-end", message.moduleName);
                break;
            case "mobileAppInfo":
                chrome.notifications.create({
                    type: "basic",
                    title: "Mobile App Info",
                    message: message.detail,
                    iconUrl: "icon.png"
                });
                GlobalEvents.emit("mobile-info", message.detail);
                break;
        }
    };

    ws.onclose = function (event) {
        console.log("WebSocket closed", event.code, event.reason);
        GlobalEvents.emit("connection-closed", { code: event.code, reason: event.reason });
    };
}

