class WebSocketClient {
  constructor(url) {
    this.DISCONNECT_TIMEOUT = 30000;
    this.RECONNECT_TIMEOUT = 5000;
    this.wsState = WebSocket.CLOSED;
    this.ws = null;
    this.wsURL = url;
    this.connectTS = 0;
    this.disconnectTID = null;
    this.reconnectTID = null;
    this.onmessage = null;
  }

  onconnect() {
    if (this.wsState !== WebSocket.CLOSED && this.wsState !== WebSocket.CLOSING) {
      return false;
    }
    this.ws = new WebSocket(this.wsURL);
    this.wsState = WebSocket.CONNECTING;
    this.connectTS = Date.now();

    this.ws.onopen = (e) => this.onopen(e);
    this.ws.onmessage = (e) => {
      if (this.onmessage) {
        this.onmessage(e);
      }
    };
    this.ws.onclose = () => this.onclose();
    this.ws.onerror = () => this.onclose();

    return true;
  }

  ondisconnect() {
    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }
    clearTimeout(this.disconnectTID);
    clearTimeout(this.reconnectTID);
    this.wsState = WebSocket.CLOSED;
  }

  onopen(e) {
    this.wsState = WebSocket.OPEN;
    clearTimeout(this.disconnectTID);
    this.disconnectTID = setTimeout(() => this.ondisconnect(), this.DISCONNECT_TIMEOUT);
  }

  onclose() {
    this.wsState = WebSocket.CLOSED;
    this.reconnectTID = setTimeout(() => {
      this.reconnect();
    }, this.RECONNECT_TIMEOUT);
    return true;
  }

  send(e) {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify(e));
    }
  }

  reconnect() {
    this.ondisconnect();
    this.onconnect();
  }
}

