class QuqiClient {
  constructor(config) {
    this.headers = {
      cookie: config.cookie,
      referer: 'https://www.quqi.com/',
      'User-Agent': 'Mozilla/5.0'
    };
    this.config = config;
    this.axiosInstance = require('axios').create({
      baseURL: 'https://www.quqi.com',
      headers: this.headers
    });
  }

  async _sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  async getDailyTask() {
    try {
      const res = await this.axiosInstance.get('/api/tasks/daily');
      return res.data;
    } catch (error) {
      console.error('Failed to get daily tasks:', error.message);
      return null;
    }
  }

  async getReward(id) {
    try {
      await this.axiosInstance.post('/api/tasks/reward', { taskId: id });
    } catch (error) {
      console.error(`Failed to get reward for task ${id}:`, error.message);
    }
  }

  async visit(id) {
    try {
      await this.axiosInstance.post('/api/groups/visit', { groupId: id });
    } catch (error) {
      console.error(`Failed to visit group ${id}:`, error.message);
    }
  }

  async getQuqiId() {
    try {
      const res = await this.axiosInstance.get('/api/groups/info');
      if (!res.data.joined) {
        await this.axiosInstance.post('/api/groups/join', { groupId: res.data.id });
      }
      return res.data;
    } catch (error) {
      console.error('Failed to get Quqi ID:', error.message);
      return null;
    }
  }

  async uploadImg(id) {
    try {
      const dummyImage = 'iVBORw0KGgoAAAANSUhEUgAAASwAAACCCAMAAADQNkiAAAAA1BMVEX///+nxBvIAAAAR0lEQVR4nO3BAQ0AAADCoPdPbQ43oAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAIBLcQ8AAa0jZQAAAABJRU5ErkJggg==';
      const uploadRes = await this.axiosInstance.post('/api/images/upload', {
        groupId: id,
        image: dummyImage
      });
      await this.axiosInstance.delete(`/api/images/${uploadRes.data.id}`);
    } catch (error) {
      console.error(`Failed to upload image for group ${id}:`, error.message);
    }
  }

  async executeTasks() {
    const tasks = await this.getDailyTask();
    if (!tasks) return 'No tasks available';

    for (const task of tasks) {
      switch (task.type) {
        case 'reward':
          await this.getReward(task.id);
          break;
        case 'visit':
          await this.visit(task.groupId);
          break;
        case 'upload':
          await this.uploadImg(task.groupId);
          break;
        default:
          console.log(`Unknown task type: ${task.type}`);
      }
      await this._sleep(1000);
    }

    try {
      const walletRes = await this.axiosInstance.get('/api/user/wallet');
      return `Wallet info: ${JSON.stringify(walletRes.data)}`;
    } catch (error) {
      return 'Failed to retrieve wallet information';
    }
  }
}

