function initializeWebSocket(handleFunctionCall, url, cb) {
  let ws;
  let reconnectAttempts = 0;

  function connect() {
    ws = new WebSocket(url);

    ws.onopen = () => {
      postMessage({ status: 'connected' });
      cb(ws);
      reconnectAttempts = 0;
    };

    ws.onmessage = (event) => {
      handleFunctionCall(event.data);
    };

    ws.onclose = (event) => {
      postMessage({ status: 'disconnected', code: event.code, reason: event.reason });
      setTimeout(() => {
        postMessage({ status: 'reconnecting', attempt: reconnectAttempts + 1 });
        reconnectAttempts++;
        connect();
      }, 5000);
    };

    ws.onerror = (error) => {
      postMessage({ status: 'error', message: error.message });
    };
  }

  connect();
}

