class ReconnectingWebSocket {
  constructor(url, protocols = null, options = {}) {
    this.url = url;
    this.protocols = protocols;
    this.debug = options.debug || false;
    this.automaticOpen = options.automaticOpen !== undefined ? options.automaticOpen : true;
    this.reconnectInterval = options.reconnectInterval || 1000;
    this.maxReconnectInterval = options.maxReconnectInterval || 30000;
    this.reconnectDecay = options.reconnectDecay || 1.5;
    this.timeoutInterval = options.timeoutInterval || 2000;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CONNECTING;
    this.protocol = null;
    this._listeners = {};
    this._ws = null;
    this._reconnectTimer = null;
    this._timeoutTimer = null;

    if (this.automaticOpen) {
      this.open();
    }
  }

  addEventListener(type, listener) {
    if (!this._listeners[type]) {
      this._listeners[type] = [];
    }
    this._listeners[type].push(listener);
  }

  removeEventListener(type, listener) {
    if (this._listeners[type]) {
      this._listeners[type] = this._listeners[type].filter(l => l !== listener);
    }
  }

  dispatchEvent(event) {
    if (!this._listeners[event.type]) return false;
    this._listeners[event.type].forEach(listener => listener(event));
    return true;
  }

  open(reconnecting = false) {
    if (this._ws) {
      this._ws.onopen = () => {};
      this._ws.onclose = () => {};
      this._ws.onerror = () => {};
      this._ws.close();
      this._ws = null;
    }

    this.readyState = WebSocket.CONNECTING;
    this.dispatchEvent(new Event('connecting'));

    this._ws = new WebSocket(this.url, this.protocols);
    this._ws.binaryType = 'arraybuffer';

    this._ws.onopen = (event) => {
      this.reconnectAttempts = 0;
      this.protocol = this._ws.protocol;
      this.readyState = WebSocket.OPEN;
      this.dispatchEvent(new Event('open'));
      this._clearTimeout();
    };

    this._ws.onclose = (event) => {
      this.readyState = WebSocket.CLOSED;
      this.dispatchEvent(new Event('close'));
      if (!reconnecting && this.reconnectInterval > 0) {
        this._scheduleReconnect();
      }
    };

    this._ws.onmessage = (event) => {
      this.dispatchEvent(new MessageEvent('message', { data: event.data }));
    };

    this._ws.onerror = (event) => {
      this.dispatchEvent(new Event('error'));
    };

    this._setTimeOut();
  }

  send(data) {
    if (this.readyState === WebSocket.OPEN) {
      this._ws.send(data);
    } else {
      throw new Error('WebSocket is not open: readyState ' + this.readyState);
    }
  }

  close(code, reason) {
    if (this._ws) {
      this._ws.close(code, reason);
    }
    this.readyState = WebSocket.CLOSING;
    this.dispatchEvent(new Event('close'));
  }

  refresh() {
    if (this._ws) {
      this._ws.close(1000);
    }
  }

  _scheduleReconnect() {
    this._clearReconnect();
    this._reconnectTimer = setTimeout(() => {
      this.reconnectAttempts++;
      const interval = Math.min(
        this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts),
        this.maxReconnectInterval
      );
      this.open(true);
    }, this.reconnectInterval);
  }

  _clearReconnect() {
    if (this._reconnectTimer) {
      clearTimeout(this._reconnectTimer);
      this._reconnectTimer = null;
    }
  }

  _setTimeOut() {
    this._clearTimeout();
    this._timeoutTimer = setTimeout(() => {
      if (this._ws && this.readyState === WebSocket.CONNECTING) {
        this._ws.close(1002);
      }
    }, this.timeoutInterval);
  }

  _clearTimeout() {
    if (this._timeoutTimer) {
      clearTimeout(this._timeoutTimer);
      this._timeoutTimer = null;
    }
  }
}

