class LiveQueryClient {
  constructor({ applicationId, serverURL, javascriptKey, masterKey, sessionToken, installationId }) {
    this.attempts = 0;
    this.id = null;
    this.requestId = 0;
    this.applicationId = applicationId;
    this.serverURL = serverURL;
    this.javascriptKey = javascriptKey;
    this.masterKey = masterKey;
    this.sessionToken = sessionToken;
    this.installationId = installationId;
    this.additionalProperties = false;
    this.connectPromise = null;
    this.subscriptions = new Map();
    this.socket = null;
    this.state = "INITIALIZED";
    this.reconnectHandle = null;
    this.emitter = new EventEmitter();
    this.on = this.emitter.on.bind(this.emitter);
    this.emit = this.emitter.emit.bind(this.emitter);

    if (!/^wss?:/.test(serverURL)) {
      throw new Error("Server URL must start with 'ws' or 'wss'");
    }
  }

  open() {
    this.state = "CONNECTING";
    this.attempts = 0;
    this.socket = new WebSocket(this.serverURL);

    this.socket.onopen = () => this._handleWebSocketOpen();
    this.socket.onmessage = (event) => this._handleWebSocketMessage(event);
    this.socket.onclose = () => this._handleWebSocketClose();
    this.socket.onerror = (error) => this._handleWebSocketError(error);

    this.connectPromise = new Promise((resolve, reject) => {
      this.resolveConnect = resolve;
      this.rejectConnect = reject;
    });
  }

  _handleWebSocketOpen() {
    const authData = {
      op: "connect",
      data: {
        applicationId: this.applicationId,
        javascriptKey: this.javascriptKey,
        masterKey: this.masterKey,
        sessionToken: this.sessionToken,
        installationId: this.installationId
      }
    };
    this.socket.send(JSON.stringify(authData));
    this.emit("open");
  }

  _handleWebSocketMessage(event) {
    const message = JSON.parse(event.data);

    if (message.op === "connected") {
      this.id = message.data.clientId;
      this.state = "CONNECTED";
      this.attempts = 0;
      this.resolveConnect();

      // Resubscribe all pending subscriptions
      this.subscriptions.forEach(subscription => {
        this._sendSubscriptionRequest(subscription);
      });
    } else if (message.op === "create" || message.op === "update" || message.op === "delete") {
      const subscription = this.subscriptions.get(message.data.requestId);
      if (subscription) {
        subscription.emit(message.op, message.data.object);
      }
    }

    this.emit("message", message);
  }

  _handleWebSocketClose() {
    this.state = "DISCONNECTED";
    this.emit("close");
    this._scheduleReconnect();
  }

  _handleWebSocketError(error) {
    this.emit("error", error);
    this._scheduleReconnect();
  }

  _scheduleReconnect() {
    if (this.reconnectHandle) return;

    const delay = Math.min(1000 * Math.pow(2, this.attempts), 30000);
    this.reconnectHandle = setTimeout(() => {
      this.reconnectHandle = null;
      this.open();
    }, delay);

    this.attempts++;
  }

  _sendSubscriptionRequest(subscription) {
    this.requestId++;
    const query = subscription.query.toJSON();

    const request = {
      op: "subscribe",
      data: {
        className: query.className,
        query: query._where || {},
        requestId: this.requestId
      }
    };

    this.socket.send(JSON.stringify(request));

    subscription._requestId = this.requestId;
    this.subscriptions.set(this.requestId, subscription);
  }
}

