class WebSocketStore {
  constructor(snack, currentUser) {
    this.snack = snack;
    this.currentUser = currentUser;
    this.wsActive = false;
    this.ws = null;
    this.reconnectTimer = null;
  }

  listen(callback) {
    if (this.ws) {
      this.ws.close(1000);
    }

    const token = this.currentUser.getToken();
    this.ws = new WebSocket(`wss://example.com/socket?token=${token}`);

    this.ws.onopen = () => {
      this.wsActive = true;
      this.reconnectTimer = null;
    };

    this.ws.onmessage = (event) => {
      try {
        const msg = JSON.parse(event.data);
        callback(msg);
      } catch (e) {
        this.snack.reportError('Failed to parse WebSocket message', e);
      }
    };

    this.ws.onerror = (error) => {
      this.snack.reportError('WebSocket error', error);
    };

    this.ws.onclose = (event) => {
      this.wsActive = false;
      this.ws = null;

      if (event.code !== 1000) {
        this.reconnectTimer = setTimeout(() => {
          this.listen(callback);
        }, 5000);
      }
    };
  }

  close() {
    if (this.ws) {
      this.ws.close(1000);
    }
    if (this.reconnectTimer) {
      clearTimeout(this.reconnectTimer);
      this.reconnectTimer = null;
    }
  }
}

