      id: this.utils.uuid(),
      method: opts.name,
      params: opts.params && opts.params.length ? opts.params : undefined
    };

    if (data.params && !data.params.length) data.params = undefined;

    this.handles.push({
      success: opts.success || angular.noop,
      error: opts.error || angular.noop,
      id: data.id
    });
