    ws = new WebSocket(`ws://${data.host}:${data.port}`);

    ws.onopen = () => {
      const config = {
        language: data.language,
        task: data.task,
        modelSize: data.modelSize,
        useVad: data.useVad
      };
      ws.send(JSON.stringify(config));
    };

    ws.onmessage = (event) => {
      try {
        const message = JSON.parse(event.data);
        if (message.status === 'ready') {
          isServerReady = true;
          startAudioCapture();
        } else if (message.type === 'transcription') {
          browser.runtime.sendMessage({ type: 'transcription', text: message.text });
        } else if (message.type === 'status') {
          browser.runtime.sendMessage({ type: 'status', status: message.status });
        }
      } catch (e) {
        console.error('Error parsing message:', e);
      }
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      browser.runtime.sendMessage({ type: 'error', message: 'WebSocket error' });
      stopAudioCapture();
    };

    ws.onclose = () => {
      console.log('WebSocket closed');
      browser.runtime.sendMessage({ type: 'status', status: 'disconnected' });
      stopAudioCapture();
    };
  }

  function startAudioCapture() {
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then(stream => {
        mediaRecorder = new MediaRecorder(stream);
        mediaRecorder.ondataavailable = event => {
          if (event.data.size > 0 && isServerReady && ws && ws.readyState === WebSocket.OPEN) {
            try {
              ws.send(event.data);
            } catch (e) {
