class CocogoatWebControl {
  port: number;
  token: string;
  hwnd: number;
  version: string;
  ev: any;
  client: any;
  ws: WebSocket | undefined;
  private _pendingRequests: Map<string, { resolve: (value: { status: number, body: any }) => void, reject: (reason?: any) => void }>;
  private _requestIdCounter: number;

  constructor(_port?: number) {
    this.port = _port || 32333;
    this.token = '';
    this.hwnd = 0;
    this.version = '';
    this.ev = new Mitt();
    this.client = new FlyType(`http://localhost:${this.port}`);
    this._pendingRequests = new Map();
    this._requestIdCounter = 0;
  }

  async authorize(): Promise<boolean> {
    try {
      const response = await this.client.post('/auth', { credentials: 'example' });
      this.token = response.data.token;

      this.ws = new WebSocket(`ws://localhost:${this.port}/?token=${this.token}`);

      return new Promise((resolve, reject) => {
        this.ws.onopen = () => {
          this._setupWebSocketHandlers();
          resolve(true);
        };
        this.ws.onerror = (event) => {
          reject(false);
        };
        this.ws.onclose = (event) => {
          reject(false);
        };
      });
    } catch (error) {
      return false;
    }
  }

  async wsInvoke(method: string, path: string, querystring?: Record<string, any>): Promise<{ status: number, body: any }> {
    if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
      throw new Error('WebSocket is not open');
    }

    const requestId = this._generateRequestId();
    const message = {
      method,
      path,
      querystring,
      request_id: requestId
    };

    return new Promise((resolve, reject) => {
      this._pendingRequests.set(requestId, { resolve, reject });
      this.ws.send(JSON.stringify(message));
    });
  }

  private _generateRequestId(): string {
    return String(this._requestIdCounter++);
  }

  private _setupWebSocketHandlers() {
    if (!this.ws) return;

    this.ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        if (data.request_id) {
          const handler = this._pendingRequests.get(data.request_id);
          if (handler) {
            if (data.error) {
              handler.reject(data.error);
            } else {
              handler.resolve({ status: data.status, body: data.body });
            }
            this._pendingRequests.delete(data.request_id);
          }
        } else {
          this.ev.emit('message', data);
        }
      } catch (error) {
        console.error('Error parsing WebSocket message:', error);
      }
    };

    this.ws.onclose = () => {
      this.ev.emit('close', {});
    };

    this.ws.onerror = (event) => {
      this.ev.emit('error', event);
    };
  }
}

