class WebSocketClient {
  constructor() {
    this.wsClient = null;
    this.sendQueue = [];
    this.pendingReconnect = null;
    this.onOpenCallback = null;
    this.onCloseCallback = null;
    this.onMessageCallback = null;
  }

  fireSendQueue() {
    while (this.sendQueue.length > 0) {
      const { data, resolve, reject } = this.sendQueue.shift();
      try {
        this.wsClient.send(data);
        resolve();
      } catch (error) {
        reject(error);
        break;
      }
    }
  }

  clearSendQueue() {
    for (const { reject } of this.sendQueue) {
      reject(new Error("Connection closed before message could be sent"));
    }
    this.sendQueue = [];
  }

  planToReconnect(rpcUrl, options) {
    if (this.pendingReconnect) clearTimeout(this.pendingReconnect);
    const delay = options.reconnectInterval || 5000;
    this.pendingReconnect = setTimeout(() => {
      this.reconnect(rpcUrl, options);
