const WebSocket = require('ws');

class SnapdropServer {
  constructor(port) {
    this._wss = new WebSocket.Server({ port });
    this._rooms = {};
    this._peerIdCounter = 0;

    this._wss.on('connection', (peer) => this._onConnection(peer));
    this._wss.on('headers', (headers, request) => this._onHeaders(headers, request));
  }

  _onHeaders(headers, request) {
    headers.push('Set-Cookie: session=abc123');
  }

  _onConnection(peer) {
    const ip = (peer.upgradeReq.headers['x-forwarded-for'] || peer.upgradeReq.connection.remoteAddress).split(':')[0];
    peer.ip = ip;
    peer.id = `peer-${this._peerIdCounter++}`;

    this._joinRoom(peer);

    peer.on('message', (message) => this._onMessage(peer, message));
    peer.on('close', () => {
      clearInterval(peer.keepAliveInterval);
      if (peer.pongTimeout) clearTimeout(peer.pongTimeout);
      this._leaveRoom(peer);
    });

    this._keepAlive(peer);
  }

  _onMessage(sender, message) {
    try {
      const data = JSON.parse(message);
      if (data.type === 'hello') {
        this._broadcastToRoom(sender, { type: 'new-peer', id: sender.id });
      } else if (data.type === 'data') {
        this._broadcastToRoom(sender, data);
      }
    } catch (e) {
      console.error('Invalid JSON message:', message);
    }
  }

  _send(peer, message) {
    if (peer.readyState === WebSocket.OPEN) {
      peer.send(JSON.stringify(message));
    }
  }

  _joinRoom(peer) {
    if (!this._rooms[peer.ip]) this._rooms[peer.ip] = [];
    this._rooms[peer.ip].push(peer);
  }

  _leaveRoom(peer) {
    const room = this._rooms[peer.ip];
    if (room) {
      const index = room.indexOf(peer);
      if (index !== -1) {
        room.splice(index, 1);
        if (room.length === 0) delete this._rooms[peer.ip];
      }
    }
  }

  _keepAlive(peer) {
    peer.keepAliveInterval = setInterval(() => {
      if (peer.readyState === WebSocket.OPEN) peer.ping();
    }, 30000);

    peer.pongTimeout = null;
    peer.on('pong', () => {
      if (peer.pongTimeout) clearTimeout(peer.pongTimeout);
      peer.pongTimeout = setTimeout(() => {
        peer.terminate();
      }, 60000);
    });
  }

  _broadcastToRoom(sender, message) {
    const room = this._rooms[sender.ip];
    if (room) {
      room.forEach((peer) => {
        if (peer !== sender && peer.readyState === WebSocket.OPEN) {
          this._send(peer, message);
        }
      });
    }
  }
}
