        const url = new URL(`http://localhost${req.url}`);
        if (url.searchParams.get('secret') !== this.secret) {
          ws.close();
          return;
        }
        this.connections.add(ws);
        ws.on('close', () => this.connections.delete(ws));
      } catch {
        ws.close();
      }
    });

