const WebSocket = require('ws');

class AphlictClientServer {
  constructor(server) {
    this._server = server;
    this._lists = {};
    this._adminServers = [];
    server.on('request', (req, res) => this._onrequest(req, res));
  }

  _onrequest(request, response) {
    response.writeHead(501);
    response.end('Not Implemented');
  }

  _parseInstanceFromPath(path) {
    const basePath = path.split('?')[0];
    const parts = basePath.split('/');
    if (parts[1] === 'aphlict' && parts.length >= 3) {
      return parts[2];
    }
    return 'default';
  }

  listen() {
    this._wss = new WebSocket.Server({ server: this._server });
    this._wss.on('connection', (ws, request) => {
      const instance = this._parseInstanceFromPath(request.url);
      ws.instance = instance;
      this.getListenerList(instance).push(ws);
      this.log(`Client connected to instance ${instance}`);
      ws.on('message', (data) => {
        this._handleMessage(ws, data);
      });
      ws.on('close', () => {
        this._handleClose(ws);
      });
    });
  }

  getListenerList(instance) {
    if (!this._lists[instance]) {
      this._lists[instance] = [];
    }
    return this._lists[instance];
  }

  log(message) {
    console.log(`AphlictClientServer: ${message}`);
  }

  trace(message) {
    console.trace(`AphlictClientServer: ${message}`);
  }

  _handleMessage(ws, data) {
    try {
      const msg = JSON.parse(data);
      switch (msg.type) {
        case 'subscribe':
          this.log(`Subscribe to instance ${ws.instance}`);
          break;
        case 'unsubscribe':
          this._removeFromInstanceList(ws);
          break;
        case 'replay':
          const history = this.getHistory(msg.min_age);
          history.forEach(h => ws.send(JSON.stringify(h)));
          break;
        case 'ping':
          ws.send(JSON.stringify({ type: 'pong' }));
          break;
        default:
          this.log(`Unknown message type: ${msg.type}`);
      }
    } catch (e) {
      this.log(`Error parsing message: ${e.message}`);
    }
  }

  _handleClose(ws) {
    this._removeFromInstanceList(ws);
    this.log(`Client disconnected from instance ${ws.instance}`);
  }

  _removeFromInstanceList(ws) {
    const instance = ws.instance;
    const list = this.getListenerList(instance);
    const index = list.indexOf(ws);
    if (index !== -1) {
      list.splice(index, 1);
    }
  }

  getHistory(min_age) {
    return [];
  }
}

module.exports = AphlictClientServer;

