const axios = require('axios');
const fs = require('fs');
const path = require('path');

function downloadIcons(url, style, callback) {
  axios.get(url, {
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json'
    },
    withCredentials: true
  })
  .then(response => {
    const rawIcons = response.data.icons;
    const processedIcons = processIcons(rawIcons, style);
    const promises = processedIcons.map(icon => {
      return axios.get(icon.svgUrl)
        .then(svgResponse => {
          const svgContent = svgResponse.data;
          const fileName = `${icon.name}.svg`;
          const filePath = path.join(__dirname, 'icons', fileName);
          let uniqueFilePath = filePath;
          let counter = 1;
          while (fs.existsSync(uniqueFilePath)) {
            const base = path.basename(filePath, '.svg');
            uniqueFilePath = path.join(__dirname, 'icons', `${base}-${counter}.svg`);
            counter++;
          }
          fs.writeFileSync(uniqueFilePath, svgContent);
          icon.svg = svgContent;
        })
        .catch(err => {
          console.error(`Error downloading SVG for ${icon.name}:`, err.message);
          throw err;
        });
    });
    return Promise.all(promises).then(() => processedIcons);
  })
  .then(processedIcons => {
    const sortedIcons = processedIcons.sort((a, b) => a.name.localeCompare(b.name));
    callback(null, sortedIcons);
  })
  .catch(err => {
    callback(err);
  });
}

function processIcons(icons, style) {
  const nameCounts = {};
  return icons.map(icon => {
    let name = icon.name;
    if (nameCounts[name]) {
      nameCounts[name]++;
      name = `${name}-${nameCounts[name]}`;
    } else {
      nameCounts[name] = 1;
    }
    return {
      name,
      category: icon.category || '',
      tags: Array.isArray(icon.tags) ? icon.tags : [],
      code: icon.code || '',
      unicode: icon.unicode || '',
      pro: icon.pro || false,
      style,
      svg: ''
    };
  });
}
