    if (this._xhr) {
      this._requestAbort = true;
      this._xhr.abort();
    }
  }

  _onReadyStateChange(e) {
    const xhr = this._xhr;
    if (!xhr) return;
    if (xhr.readyState === 2) {
      let contentLength = null;
      const contentRange = xhr.getResponseHeader('Content-Range');
      if (xhr.status === 206 && contentRange) {
        const totalMatch = contentRange.match(/\/(\d+)$/);
        if (totalMatch) {
