  const key = `${instrument}/${name}`;
  if (getNote.cache.has(key)) return getNote.cache.get(key);

  const fullUrl = `${url}${instrument}/${name}`;
  const xhr = new XMLHttpRequest();
  xhr.open("GET", fullUrl);
  xhr.responseType = "arraybuffer";

  const promise = new Promise((resolve, reject) => {
    xhr.onload = function () {
      if (xhr.status >= 200 && xhr.status < 300) {
        audioContext.decodeAudioData(xhr.response)
          .then(decodedBuffer => {
            resolve({
              instrument,
              name,
              status: "loaded",
              audioBuffer: decodedBuffer
            });
          })
          .catch(decodeError => {
            reject(new Error(`Failed to decode audio: ${fullUrl}`, { cause: decodeError }));
          });
      } else {
        reject(new Error(`HTTP error ${xhr.status} loading ${fullUrl}`));
      }
    };

    xhr.onerror = function () {
      reject(new Error(`Network error loading ${fullUrl}`));
    };

    xhr.send();
  });

