function fetchRequest(e, n) {
  return new Promise((resolve, reject) => {
    const xhr = new XMLHttpRequest();
    const config = n || {};
    const method = (config.method || "GET").toUpperCase();
    const url = e;
    const headers = config.headers || {};
    const credentials = config.credentials;

    xhr.open(method, url, true);
    if (credentials) {
      xhr.withCredentials = credentials === "include" || credentials === "same-origin";
    }

    for (const key in headers) {
      if (headers.hasOwnProperty(key)) {
        xhr.setRequestHeader(key, headers[key]);
      }
    }

    xhr.responseType = "blob";

    xhr.onload = () => {
      if (xhr.readyState === 4) {
        const headersMap = {};
        const rawHeaders = xhr.getAllResponseHeaders();
        if (rawHeaders) {
          const lines = rawHeaders.trim().split("\n");
          for (const line of lines) {
            const [key, ...values] = line.split(":").map(part => part.trim());
            headersMap[key.toLowerCase()] = values.join(":");
          }
        }

        const response = {
          ok: xhr.status >= 200 && xhr.status < 300,
          status: xhr.status,
          statusText: xhr.statusText,
          url: xhr.responseURL,
          blob: () => Promise.resolve(xhr.response),
          text: () => {
            return new Promise((resolveText, rejectText) => {
              const reader = new FileReader();
              reader.onload = () => resolveText(reader.result);
              reader.onerror = () => rejectText(reader.error);
              reader.readAsText(xhr.response);
            });
          },
          json: () => response.text().then(JSON.parse),
          clone: () => {
            return {
              ok: response.ok,
              status: response.status,
              statusText: response.statusText,
              url: response.url,
              blob: response.blob,
              text: response.text,
              json: response.json,
              clone: response.clone,
              headers: {
                keys: response.headers.keys,
                entries: response.headers.entries,
                get: response.headers.get,
                has: response.headers.has
              }
            };
          },
          headers: {
            keys: () => Object.keys(headersMap),
            entries: () => Object.entries(headersMap),
            get: name => headersMap[name.toLowerCase()],
            has: name => name.toLowerCase() in headersMap
          }
        };

        resolve(response);
      }
    };

    xhr.onerror = () => reject(new Error("Network error"));
    xhr.send(config.body);
  });
}

