const express = require('express');
const http = require('http');
const session = require('express-session');
const cookieParser = require('cookie-parser');
const Primus = require('primus');
const cookie = require('cookie');
const sign = require('sign');

function setupExpressServer() {
  const app = express();
  const server = http.createServer(app);
  const secret = 'your-secret';

  app.use(cookieParser(secret));
  const sessionMiddleware = session({
    secret,
    resave: false,
    saveUninitialized: true
  });
  app.use(sessionMiddleware);

  app.get('/', (req, res) => {
    req.session.views = (req.session.views || 0) + 1;
    res.send(`Views: ${req.session.views}`);
  });

  const primus = new Primus(server, { transformer: 'websockets' });

  primus.on('connection', (spark) => {
    const headers = spark.request.headers;
    const cookieHeader = headers.cookie;

    if (cookieHeader) {
      const cookies = cookie.parse(cookieHeader);
      const sidCookie = cookies['connect.sid'];
      const sid = sign.unsign(sidCookie, secret);

      if (sid) {
        sessionMiddleware.store.get(sid, (err, sessionData) => {
          if (!err && sessionData) {
            spark.session = sessionData;
          }
        });
      }
    }

    spark.on('data', (data) => {
      spark.write(`Echo: ${data}`);
    });
  });

  server.listen(8080, () => {
    console.log('Server listening on port 8080');
  });
}

