class FOAAS {
  constructor(options) {
    this.app = express();
    this.operations = {
      'goaway': { code: 418, message: 'Go away, %s!' },
      'notfound': { code: 404, message: 'Not found, %s.' },
      'teapot': { code: 418, message: "I'm a teapot, %s!" }
    };
    this.operationsArray = Object.keys(this.operations).map(key => ({
      name: key,
      ...this.operations[key]
    }));

    this.app.use(bodyParser.json());
    this.app.use(cors());

    this.app.get('/:operation/:name?', (req, res) => {
      const op = req.params.operation;
      const name = req.params.name || 'you';
      const operation = this.operations[op];
      if (!operation) {
        res.status(404);
        return this.output(req, res, 'Operation not found', `Unknown operation: ${op}`);
      }
      res.status(operation.code);
      const message = operation.message.replace(/%s/g, name);
      this.output(req, res, message, `-${name}`);
    });

    this.app.get('/', this.sendIndex.bind(this));
  }

  sendIndex(req, res) {
    res.sendFile(path.join(__dirname, 'index.html'));
  }

  start(port) {
    this.app.listen(port, () => {
      console.log(`FOAAS server is running on port ${port}`);
    });
