    try {
      const uploadedFilePath = req.file.path;
      const originalName = req.file.originalname;
      const outputFileName = `processed_${originalName.replace(/\.[^/.]+$/, '')}.jpg`;
      const outputFilePath = path.join('public', 'processed', outputFileName);

