function setupExpressServer() {
    const express = require('express');
    const connect = require('connect');
    const fs = require('fs');
    const nodemailer = require('nodemailer');
    const lcrypto = require('lcrypto');
    const lutil = require('lutil');
    const auth = global.auth;
    const processInfo = global.processInfo;
    const locker = global.locker;
    const request = require('request');

    const app = express();

    app.use(express.json());
    app.use(express.urlencoded({ extended: true }));
    app.use(connect.cookieParser());

    app.post('/auth', (req, res) => {
        const { username, password } = req.body;
        if (username === auth.username && lcrypto.decrypt(password) === auth.password) {
            res.cookie('auth_token', lcrypto.generateToken(), { httpOnly: true });
            res.status(200).send({ message: 'Authentication successful' });
        } else {
            res.status(401).send({ error: 'Invalid credentials' });
        }
    });

    app.get('/test-email', (req, res) => {
        if (!req.cookies.auth_token || !lcrypto.verifyToken(req.cookies.auth_token)) {
            return res.status(403).send({ error: 'Unauthorized access' });
        }

        const transporter = nodemailer.createTransport({
            host: 'smtp.example.com',
            port: 587,
            secure: false,
            auth: {
                user: 'test@example.com',
                pass: 'password'
            }
        });

        const mailOptions = {
            from: 'test@example.com',
            to: 'recipient@example.com',
            subject: 'Test Email',
            text: 'This is a test email sent via the Express server.'
        };

        transporter.sendMail(mailOptions, (error, info) => {
            if (error) {
                return res.status(500).send({ error: 'Failed to send test email' });
            }
            res.status(200).send({ message: 'Test email sent successfully', info });
        });
    });

    app.post('/save-config', (req, res) => {
        if (!req.cookies.auth_token || !lcrypto.verifyToken(req.cookies.auth_token)) {
            return res.status(403).send({ error: 'Unauthorized access' });
        }

        const config = req.body;
        lutil.writeJSONFile('./config/emailConfig.json', config);

        res.status(200).send({ message: 'Email server configuration saved' });
    });

    app.get('/get-config', (req, res) => {
        if (!req.cookies.auth_token || !lcrypto.verifyToken(req.cookies.auth_token)) {
            return res.status(403).send({ error: 'Unauthorized access' });
        }

        try {
            const config = lutil.readJSONFile('./config/emailConfig.json');
            res.status(200).send(config);
        } catch (error) {
            res.status(404).send({ error: 'Configuration not found' });
        }
    });

    app.listen(processInfo.port, () => {
        console.log(`Server running on port ${processInfo.port}`);
    });
}
