const express = require('express');
const fs = require('fs');
const path = require('path');
const responseTime = require('response-time');

function setupExpressServer() {
  const app = express();
  const filePath = path.join(__dirname, 'CapitalOne_Digital_Skills_Infographic_BW.jpg');
  const port = 3000;

  app.use(responseTime());

  // Streaming endpoint (1)
  app.get('/stream', (req, res) => {
    const stream = fs.createReadStream(filePath);
    stream.pipe(res);
    stream.on('error', (err) => {
      res.status(500).send('Error reading file');
    });
  });

  // Non-streaming endpoint (2)
  app.get('/nonstream', (req, res) => {
    fs.readFile(filePath, (err, data) => {
      if (err) {
        return res.status(500).send('Error reading file');
      }
      res.send(data);
    });
  });

  // Streaming with custom headers (3)
  app.get('/stream-custom-headers', (req, res) => {
    res.setHeader('Content-Type', 'image/jpeg');
    const stream = fs.createReadStream(filePath);
    stream.pipe(res);
    stream.on('error', (err) => {
      res.status(500).send('Error reading file');
    });
  });

  // Non-streaming with custom headers (4)
  app.get('/nonstream-custom-headers', (req, res) => {
    res.setHeader('Content-Type', 'image/jpeg');
    fs.readFile(filePath, (err, data) => {
      if (err) {
        return res.status(500).send('Error reading file');
      }
      res.send(data);
    });
  });

  app.listen(port, () => {
    console.log(`Server running at http://localhost:${port}`);
  });
}
