const fetch = require('node-fetch');

async function getWikipediaLinks(keyword) {
  const searchUrl = `https://en.wikipedia.org/w/api.php?action=opensearch&search=${encodeURIComponent(keyword)}&limit=5&namespace=0&format=json`;
  const response = await fetch(searchUrl);
  const data = await response.json();
  return data[1]; // Titles
}

async function getSummaryAndCategories(title) {
  const pageUrl = `https://en.wikipedia.org/w/api.php?action=query&titles=${encodeURIComponent(title)}&prop=extracts|categories&exintro=true&explaintext=true&format=json`;
  const response = await fetch(pageUrl);
  const data = await response.json();
  const page = Object.values(data.query.pages)[0];
  return {
    title: page.title,
    summary: page.extract || 'No summary available.',
    categories: page.categories ? page.categories.map(cat => cat.title) : []
  };
}

